		for (int i = 0; i < baseAmount.size(); i++) {
			totalAmount2 = totalAmount2
					+ Double.parseDouble(baseAmount.get(i)) * Double.parseDouble(totalNights.get(i));
		}
		String roomChargeExp = df.format(totalAmount2);
